		m_List->SetColumnWidth(0, -1);
		m_List->SetColumnWidth(1, -1);
		wxFileConfig *config = new wxFileConfig( wxT(""), wxT(""), wxPathOnly(wxStandardPaths::Get().GetExecutablePath()) + wxT("\\Data\\Variables.ini") );
		wxFileConfig::Set( config );
